import scrapy
import re

from bysjproject.items import BysjprojectItem


class ProjectSpider(scrapy.Spider):
    name = 'project'
    allowed_domains = ['bysj.jwc.sjtu.edu.cn']

    def start_requests(self):
        url = "http://bysj.jwc.sjtu.edu.cn/index.aspx"
        post_data = {
            "__VIEWSTATE": "",
            "__VIEWSTATEGENERATOR": "90059987",
            "__EVENTVALIDATION": "/wEWCQLWrY7BCgLr/4HeAgLmmdGVDAL+jNCfDwK4ovrCAQKdw8XHCAL0uc/cCgLW4JOwDwL4oeqeCcVu583ur2LZLPjfOqwHBkUUnfNV",
            "UserId": "10367",
            "Pwd": "10367",
            "LoginButton.x": "13",
            "LoginButton.y": "6"
        }
        yield scrapy.FormRequest(url=url,
                                 formdata=post_data,
                                 callback=self.character,
                                 method='POST'
                                 )

    def character(self, response):
        url = "http://bysj.jwc.sjtu.edu.cn/LoginRole.aspx?sid=Jk7UDmg%2fcSOg6wXocnYmog....&screen=C3R1RXfU%2fLYcx2ms2xw7Cg...."
        post_data = {
            "__VIEWSTATE": "/wEPDwUKMTk2MTAzODE4Mw9kFgICAw9kFgICAw8PFgIeB1Zpc2libGVnZGQYAQUeX19Db250cm9sc1JlcXVpcmVQb3N0QmFja0tleV9fFgIFDEltYWdlQnV0dG9uMwUMSW1hZ2VCdXR0b240/IfDl20NUC4dCF++M3glQRSC1r8=",
            "__VIEWSTATEGENERATOR": "6CA0729C",
            "__EVENTVALIDATION": "/wEWAwL7oojgAgLSwsGJCgLSwv2aBF/cHhCiQ2K1WnQPUn14ruv9Znzy",
            "ImageButton4.x": "43",
            "ImageButton4.y": "22"
        }
        yield scrapy.FormRequest(url=url,
                                 formdata=post_data,
                                 callback=self.parse,
                                 method='POST'
                                 )

    def parse(self, response):
        url = "http://bysj.jwc.sjtu.edu.cn/Admin/kaiticxlist.aspx?sid=Jk7UDmg/cSOg6wXocnYmog....&screen=Qd2HRMrs8oJGtA82doE7Jg....&type=zyyes&zhuanyeno=537"

        yield scrapy.Request(url=url, callback=self.parse_first)

    def parse_first(self, response):
        n = len(response.xpath('//*[@id="gridview"]/tr').extract())

        for i in range(2, n + 1):
            item = BysjprojectItem()
            item['projectname'] = response.xpath('//*[@id="gridview"]/tr[' + str(i) + ']/td[2]/font/a/text()').extract()[0].strip()
            item['teachername'] = response.xpath('//*[@id="gridview"]/tr[' + str(i) + ']/td[3]/font/text()').extract()[0].strip()
            numstring = response.xpath('//*[@id="gridview"]/tr[' + str(i) + ']/td[3]/font/text()').extract()[1].strip()
            item['teachernum'] = "".join(re.findall(r'[(](.*?)[)]', numstring))
            numstring = response.xpath('//*[@id="gridview"]/tr[' + str(i) + ']/td[4]/font/a/text()').extract()[1].strip()
            item['studentnum'] = "".join(re.findall(r'[(](.*?)[)]', numstring))
            yield item

        string = response.xpath('//*[@id="LblPageCount"]/text()').extract()[0].strip()
        num = int("".join(re.findall('\d+', string)))
        url = 'http://bysj.jwc.sjtu.edu.cn/Admin/kaiticxlist.aspx?sid=Jk7UDmg%2fcSOg6wXocnYmog....&screen=Qd2HRMrs8oJGtA82doE7Jg....&type=zyyes&zhuanyeno=537'

        for i in range(1, num):
            post_data = {
                '__EVENTTARGET': 'btnNext',
                '__VIEWSTATE': '',
                '__VIEWSTATEGENERATOR': '4D5B9866',
                '__EVENTVALIDATION': '/wEWDgKY5p+XDALwpZ2dDAKYo9WKCQLavbvPCQK4lJzYAgLF2//gBwLM9PumDwK14fOOCgKx4bePCQLP/qqSDwLAkYD8AwLBkYD8AwLCkYD8AwLDkYD8Azu0OFeiQCcV8b2OLK5ZMlyOGLnM',
                'ddlCurrentPage': str(i)
            }
            yield scrapy.FormRequest(url=url,
                                     formdata=post_data,
                                     callback=self.parse_next,
                                     method='POST'
                                     )

    def parse_next(self, response):
        n = len(response.xpath('//*[@id="gridview"]/tr').extract())

        for i in range(2, n + 1):
            item = BysjprojectItem()
            item['projectname'] = \
            response.xpath('//*[@id="gridview"]/tr[' + str(i) + ']/td[2]/font/a/text()').extract()[0].strip()
            item['teachername'] = response.xpath('//*[@id="gridview"]/tr[' + str(i) + ']/td[3]/font/text()').extract()[
                0].strip()
            numstring = response.xpath('//*[@id="gridview"]/tr[' + str(i) + ']/td[3]/font/text()').extract()[1].strip()
            item['teachernum'] = "".join(re.findall(r'[(](.*?)[)]', numstring))
            numstring = response.xpath('//*[@id="gridview"]/tr[' + str(i) + ']/td[4]/font/a/text()').extract()[
                1].strip()
            item['studentnum'] = "".join(re.findall(r'[(](.*?)[)]', numstring))
            yield item




